<?phpnamespace App\Controller\Api;use App\Entity\Event;use Symfony\Component\HttpFoundation\Request;use Symfony\Component\HttpFoundation\Response;use Symfony\Component\Validator\Validator\ValidatorInterface;/** * Class EventController */class EventController extends AbstractApiController{	/**	 * @param int $id	 *	 * @return Response	 * @throws \Exception	 */	public function getEvent(int $id): Response	{//		$data = $this->getItem($id, Event::class);		$data = [1];		return $this->view($data);	}	/**	 * @param Request            $request	 * @param ValidatorInterface $validator	 *	 * @return Response	 */	public function addEvent(Request $request, ValidatorInterface $validator): Response	{		$event = new Event();		$dataRequest = $this->getDataPost($request);		$data = $this->addItem($dataRequest, $event, $validator);		return $this->view($data);	}}