<?phpnamespace App\Service;/** * Class MailerService * @package App\Service */class MailerService{	/**	 * @var \Swift_Mailer $mailer	 */	private $mailer;	/**	 * @var \Twig_Environment $templating	 */	private $templating;	/**	 * MailerService constructor.	 *	 * @param \Swift_Mailer     $mailer	 * @param \Twig_Environment $templating	 */	public function __construct(\Swift_Mailer $mailer, \Twig_Environment $templating)	{		$this->mailer = $mailer;		$this->templating = $templating;	}	/**	 * @param string $subject	 * @param string $to	 * @param array  $data	 * @param string $template	 */	public function sendEmail(string $subject, string $to, array $data, string $template)	{		$to = 'yoann.galland71@gmail.com';		$message = (new \Swift_Message($subject))			->setFrom('yoann.galland71@gmail.com')			->setTo($to)			->setBody(				$this->templating->render(					$template,					$data				),				'text/html'			);		$this->mailer->send($message);	}}